import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import * as serviceWorker from './serviceWorker';

class MyForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      age: null,
      aadhar: '',
      gender: null
    };
  }
  myTextHandler = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    alert(val);
    this.setState({
          gender: true 
        });
    if (val.toUpperCase === "MR") {
      alert("af");
        this.setState({
          gender: true 
        });
    }
  }
  myChangeHandler = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    if (nam === "age") {
      if (!Number(val)) {
        alert("Your age must be a number");
      }
    }
    this.setState({[nam]: val});
    this.setState({[nam]: 10});
  }
  mydobHandler = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    var today = new Date();
    var birthDate = new Date(val);
    var age1 = today.getFullYear() - birthDate.getFullYear();
    this.setState({
      age: age1 
    });
  }
   mySubmitHandler = (event) => {
    event.preventDefault();
    alert("You are submitting " + this.state.username);
  }
  render() {
    return (
      <form onSubmit={this.mySubmitHandler}>
      <fieldset>
      <legend>Personal information:</legend>
      <h1>Hello {this.state.username} {this.state.age}</h1>

      <p>Enter your name:</p>
      <input type="text" name="salutation" maxlength="4" size="4" onChange={this.myTextHandler}/> 
      <input
        type='text'
        name='username'
        onChange={this.myChangeHandler}
      />
      <p>Gender:</p>
      <input type="radio" name="gender" value="male" checked={this.state.gender}/> Male
      <input type="radio" name="gender" value="female"/> Female
      <p>Enter your DOB:</p>
      <input
        type='date'
        name='bday'
        onChange={this.mydobHandler}
      />
      <p>Enter your age:</p>
      <input
        type='text'
        name='age'
        onChange={this.myChangeHandler}
        value={this.state.age}
        readOnly={true}
      />
      <p>Enter your AppoinmentDate:</p>
      <input
        type='date'
        name='appointmentdate'
      />
      <p>Enter your AadharNumber:</p>
      <input
        type='text'
        name='aadhar'
        value={this.state.aadhar}
      />
      <input
        type='submit'
      />
      </fieldset>
      </form>
    );
  }
}

ReactDOM.render(<MyForm />, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
