import React, { Component } from 'react';
import FormControl from '@material-ui/core/FormControl';

class App extends Component {
  render() {
    return (
     <form>
      <h1>Hello {this.state.username}</h1>
      <p>Enter your name:</p>
      <input
        type='text'
        onChange={this.myChangeHandler}
      />
      </form>
    );
  }
}

export default App;
